dict.string:
log('This is an example Sketch script.');

var documentName = context.document.displayName();
log('The current document is named: ' + documentName);

var selectedLayers = context.selection;
var selectedCount = selectedLayers.count();

if (selectedCount == 0) {
  log('No layers are selected.');
} else {
  log('Selected layers:');
  for (var i = 0; i < selectedCount; i++) {
    var layer = selectedLayers[i];
    log((i+1) + '. ' + layer.name());
  }
};

/* Say something nice */
@import './helper.js'

if ( ! classExists("CMD")) {
   log("⚠️ load framework first")
} else {
   var command = CMD.alloc().init();
   command.exec("say Hey people, do you hear me?")
}

/* Print current working directory */

var command = CMD.alloc().init();
log("pwd: " + command.exec("pwd"))

@import './helper.js'

if (loadFramework("PluginHelper", "/Contents/Sketch")) {
   log(" ✅ Success")
} else {
   log(" ❌ Failed to load framework")
}

/* Generate a number from 1 - 10 */
var num = Math.floor((Math.random() * 10) + 1);

if (num == 1) {
   log("you are the one");
} else if (num % 2 == 0) {
   log(num + " is even");
} else {
   log(num + " is odd");
}
